import React,{useEffect, createContext,Switch, useReducer} from 'react';
import './App.css';
import NavBar from './components/Navbar';
import {BrowserRouter, Route, Routes} from 'react-router-dom'
import Home from './components/screens/Home';
import Profile from './components/screens/Profile';
import Login from './components/screens/Signin';
import Signup from './components/screens/Signup';
import CreatePost from './components/screens/CreatePost';
import LandingPage from './components/screens/landing-page';
import PostView from './components/screens/post-view';
import { initialState, reducer } from './reducers/userReducer'
im
const userContext = createContext()


const Routing =()=>{

      const history = useHistory()
  return(
    <Switch>
      <Routes>

    <Route path="/" element={ <Home/> } />
    <Route path="/landing-page" element={ <LandingPage/> }/>
    <Route path="/post-view" element={ <PostView/> }/>
    <Route path="/login" element={ <Login/> } />
    <Route path="/signup" element={ <Signup/> } />
    <Route path="/profile" element={ <Profile/> } />
    <Route path="/createpost" element={ <CreatePost/> } />

    </Routes>
    </Switch>
  )
}


function App() {
  const [ state, dispatch] = useReducer(reducer, initialState)
  return (
    <userContext.Provider value={{state, dispatch}}>


    <BrowserRouter>
        <NavBar/>   


    </BrowserRouter>

    </userContext.Provider>
  );
}

export default App;
