import React from 'react';
import { Link } from 'react-router-dom';


const [name, setName] = useState("")
const [email, setEmail] = useState("")
const [password, setPassword] = useState("")



const Signup=()=>{
    return(
<div className='mycard'>
<div className='card auth-card'>
    <h2>Instagram</h2>
    <input type="text" placeholder="name"  value={(e)=>setName(e.target.value)} />
    <input type="text" placeholder="email" value={(e)=>setEmail(e.target.value)} />
    <input type="password" placeholder="password" value={(e)=>setPassword(e.target.value)}/>

    <button className='btn waves-effect waves-light'>Signin</button>

<h5>
    <Link to="signin">Alredy have an account?</Link>
</h5>
</div>
</div>
)
}
export default Signup;
