import React, { useEffect } from "react";
import { useState } from "react";
import M from 'materialize-css';
import {useHistory} from 'react'

const CreatePost = () => {
    
    const history = useHistory()
    const [title,setTitle] = useState("")
    const [body,setBody] = useState("")
    const [image,setImage] = useState("")
    const [url, setUrl] = useState("")
 

    useEffect(()=>{
        if(url){

        
        fetch("/createpost",{
            method: "Post",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Bearer "+localStorage.getItem("jwt")
            },
            body:JSON.stringify({
                title,
                body,
                pic:url
            })
        }).then(res=>res.json()).then(data=>{
          
            if(data.error){
                M.toast({html: data.error, classes:"#ef9a9a red lighten-3"})
            }else{
                M.toast({html:"Created post successfully", classes:"#e91e63 pink"})
                 history.push('/')
                
            }
        }).catch(err=>{
            console.log(err)
        })
    }
    },[url]);

    const postDetails = ()=>{
        const data = new FormData()
        data.append("file", image)
        data.append("upload_preset","instagramclone");
        data.append("cloud_name", "daxiadpan")
        fetch("http://api.cloudinary.com/v1_1/daxiadpan/image/upload",{
            method:"post",
            body:data
        }).then(res=>res.json()).then(data=>{
            setUrl(data.url)
        }).catch(err=>{
            console.log(err);
        })
        
       
    }


    return (
        <div className="card input-filed"
            style={{
                margin: "30px auto",
                maxwidth: "500px",
                padding: "200px",
                textAlign: "center",
                
            }}
        >
            <input type="text" placeholder="title" value={title} onChange={(e)=>setTitle(e.target.value)} />
            <input type="text" placeholder="body"  value={body} onChange={(e)=>setBody(e.target.value)}/>
            <div className="file-field input-field">
                <div className="btn">
                    <span>Upload Image</span>
                    <input type="file" onChange={(e)=>setImage(e.target.files[0])} />
                </div>
                <div className="file-path-wrapper">
                    <input class="file-path validate" type="text" />
                </div>

                <button className='btn waves-effect waves-light'
                onClick={()=>postDetails()}
                >POST</button>
            </div>



        </div>
    )
}
export default CreatePost;
