import React, {useState} from "react";

const Createpost = () => {
    const [title, setTitle] = useState("")
    const [body,setBody] = useState("")
    const [image, setImage] = useState("")


    const postDetails = ()=>{
         const data = new FormData()
         data.append("file",image)
         data.append("upload_preset","instagramclone")
         data.append("cloud_name","daxiadpan")
         fetch("https://api.cloudinary.com/v1_1/daxiadpan/image/upload",)
    }
    return (
        <div className="card input-filed"
        style={{
                
            margin:"10px auto",
            maxWidth:"500px",
            padding:"20px",
            textAlign:"center"
        }}
        >
            <input type="text" palceholder="title" value={title} onChange={(e)=>setTitle(e.target.value)} />
            <input type="text" palceholder="body"  value={body} onChange={(e)=>setBody(e.target.value)} />

            <div className="file-field input-field">
                <div className="btn ">
                    <span>Upload image</span>
                    <input type="file" onChange={(e)=>setImage(e.target.files[0])} />
                </div>
                <div className="file-path-wrapper">
                    <input className="file-path validate" type="text" />
                </div>
            </div>

            <button className='btn waves-effect waves-light' >Submit POST</button>
        </div>
    )
}

export default Createpost;
